import React from 'react';
import { render } from 'react-dom';
import { Text, StyleSheet, View, Button,TextInput ,TouchableOpacity } from 'react-native';
import { StatusBar } from 'expo-status-bar';
// import { SmoothPinCodeInput } from "react-native-smooth-pincode-input";

export default class LoginScreen extends React.Component{

    static navigationOptions = ({ navigation }) => {
        return {
          headerTitle: 'Login Screen',
        };
      };

    constructor(props) {
        super(props);
        this.state = 
        {
            password:' '
        }
        this.passwordChange=this.passwordChange.bind(this);
      }

   passwordChange(event){
       this.setState({password:event})
   }
   successfulLogin = ()=>{
       this.setState({password:''})
       this.textInput.clear()
       this.props.navigation.navigate('DrawerNav',{text:this.state.password});
   }
    render(){
        return(

            <View style={styles.container}>      
            <View style={styles.inputView}>
            <TextInput
                ref={input => { this.textInput = input }}
                style={styles.TextInput}
                placeholder="Enter Your Password"
                placeholderTextColor="#003f5c"
                secureTextEntry={true}
                onChangeText={this.passwordChange}
                keyboardType="numeric"
                maxLength={4}
            />
            </View>
            <TouchableOpacity style={styles.loginBtn} onPress={this.successfulLogin}>
                <Text style={styles.loginText}>LOGIN</Text>
            </TouchableOpacity>
            </View>
        )
    }
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    inputView: {
        backgroundColor: "#FFC0CB",
        borderRadius: 30,
        width: "70%",
        height: 45,
        marginBottom: 20,
        alignItems: "center",
      },

      TextInput: {
        height: 50,
        flex: 1,
        padding: 10,
        marginLeft: 20,
      },
      loginBtn: {
        width: "80%",
        borderRadius: 25,
        height: 50,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 40,
        backgroundColor: "#FF1493",
    }
  });
