import React from 'react';
import { Text, StyleSheet, View, Button, TouchableOpacity } from 'react-native';

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    appButtonContainer: {
      elevation: 8,
      backgroundColor: "#009688",
      borderRadius: 10,
      paddingVertical: 10,
      paddingHorizontal: 12
    },
    appButtonText: {
      fontSize: 18,
      color: "#fff",
      fontWeight: "bold",
      alignSelf: "center",
      textTransform: "uppercase"
    }
})


export default class SignInScreen extends React.Component{

  static navigationOptions = ({ navigation }) => {
    return {
      headerTitle: 'Log In/Out of Duty',
    };
  };
  constructor(props) {
    super(props);
    this.state = 
        {
            loggedin:0
        }
  }

  onButtonClick=()=>{
    let value=this.state.loggedin
    value=1-value
    this.setState({loggedin:value})
  }

  render(){
    console.log(this.props)
    let buttonmessage;
    if(!this.state.loggedin)
      buttonmessage="Login to Duty"
    else
      buttonmessage="Logout of Duty"
    return(
      <View style={styles.container}>
       <TouchableOpacity onPress={this.onButtonClick} style={styles.appButtonContainer}>
        <Text style={styles.appButtonText}>{buttonmessage}</Text>
      </TouchableOpacity>
      </View>
    )
  }
}
